"use strict";

const fs = require("fs-extra");
const path = require("path");
const tpl = require("lodash.template");

exports.genDeployXml = function(system, type, name, version) {
  const template = fs.readFileSync(
    path.resolve(__dirname, `../templates/see/1.x/deploy.xml`)
  );

  return tpl(template)({ system, type, name, version });
};

exports.genScripts = function(name, options) {
  const template = fs.readFileSync(
    path.resolve(__dirname, `../templates/see/1.x/scripts/${name}.py`)
  );

  return tpl(template)(options);
};

